mapper.select:
  select * from(
  select rownum rm, t.* from (select spv.* from sys_pda_version spv

    bmbh=#{bmbh}

AND  spv.version LIKE #{version,jdbcType=VARCHAR}


      AND  spv.description LIKE #{desc,jdbcType=VARCHAR}




  ORDER by czsj DESC) t

  ) tm where rm BETWEEN (#{page,jdbcType=INTEGER} - 1) * #{pageSize,jdbcType=INTEGER}+ 1 AND #{page,jdbcType=INTEGER} * #{pageSize,jdbcType=INTEGER}
